        @media (max-width: $reflex_xlg) {
            display: block;
        }
    }

    &.open {
        #{$self}__container {
            @media (max-width: $reflex_xlg) {

                position: fixed;
                width: 100%;
                height: 100%;
                top: 0;
                left: 0;
